  error_reporting(E_ALL);
  $mongo           = new MongoClient('mongodb://127.0.0.1');
  $movieCollection = $mongo->cinegram->movies
                      ->find(Array(),Array('ratings' => Array('$slice' => -90)))
                      ->limit(18)
                      ->sort(Array('title' => 1,'releaseYear' => 1))
                      ;

  $moviesJSON = json_encode(iterator_to_array($movieCollection,false));
  ?>

  <script>
    var movies = JSON.parse('<?= addslashes($moviesJSON) ?>');

    if(document.images)
    {
      var imgsLoaded = 0
          ,progress  = 0
          ;

      imageLoaded = function()
      {
        progress = ((++imgsLoaded / movies.length) * 100).toFixed(1);

        if(progress >= 100)
        {
          if(typeof imagesLoaded === 'function')
          {
            imagesLoaded();
          }
        }
      };

      for(var i = 0;i < movies.length;i++)
      {
        var img    = new Image();
        img.onload = imageLoaded;
        img.src    = movies[i].imageURL;
      }
    }
  </script>
